########################################################################################################################################################################
# Description: Code to load turnstile data into master dataframe and review statistics
# 
########################################################################################################################################################################
#CHANGE TO ALL_TS!
library(dplyr)
library(timeDate) 
library(reshape)
library(ggplot2)
library(data.table)
library(tidyr)
library(scales)

subwaydata <- read.csv("all_ts.csv", stringsAsFactors = FALSE)  # read csv file 

# creating dataframe with num_entries, num_exits, and time difference
names(subwaydata) <- tolower(names(subwaydata))
subwaydata$date.time <- with(subwaydata, paste(date, time, sep=' '))
subwaydata$date.time <- with(subwaydata, strptime(date.time, "%m/%d/%Y %H:%M:%S"))
subwaydata$date.time <- with(subwaydata, as.POSIXct((date.time)))
subwaydata <- mutate(subwaydata, day_of_week = dayOfWeek(as.timeDate(date.time)))

subwaydata <- as.data.frame(subwaydata) %>%select(-x, -intersect)

subwaydata <- subwaydata %>% group_by(c.a, unit, scp) %>% arrange(station_id, c.a, unit, scp)

subwaydata <- arrange(subwaydata, date.time) %>%
  mutate(lasttime = lag(date.time), time.delta = as.numeric(date.time-lag(date.time),units="hours"))

subwaydata <- arrange(subwaydata, c.a, unit, scp, station, linename, date.time)
subwaydata <-subwaydata %>%
  mutate(entry_exits_period = as.character(ifelse(time > "0:00:00" & time <= "04:00:00", as.character("0:4"),
                                                  ifelse(time > "04:00:00" & time <= "08:00:00", "4:8",
                                                         ifelse(time > "08:00:00" & time <= "12:00:00", "8:12",
                                                                ifelse(time > "12:00:00" & time <= "16:00:00", "12:16",
                                                                       ifelse(time > "16:00:00" & time <= "20:00:00", "16:20", "20:0")))))))
subwaydata <- subwaydata %>% mutate(entries.delta = entries - lag(entries), exits.delta = exits - lag(exits))
subwaydata <- ungroup(subwaydata)

subwaydata <- subwaydata %>% 
  filter(entries.delta < 100000) %>%                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
  filter(exits.delta < 100000) %>%
  filter(exits.delta > -1) %>%
  filter(entries.delta > -1) 
#.6%, I believe.
subwaydata <- filter(subwaydata, time.delta <= 12) 

#####################################################################################
#Entries, exits, stations for every day
subwaydata <- subwaydata %>% group_by(station_id, date, day_of_week, entry_exits_period) %>% arrange(station_id, date, entry_exits_period) 
daily_entries_exits <- summarize(subwaydata, st.entries = sum(entries.delta, na.rm = TRUE), st.exits = sum(exits.delta, na.rm = TRUE))
write.csv(daily_entries_exits, file = "daily_entries_exits.csv", row.names = FALSE)
#####################################################################################

entries_exits_rates <- group_by(subwaydata, station_id, entry_exits_period, date, day_of_week) %>%
  summarise(hourly_entries = sum(entries.delta)/4,hourly_exits = sum(exits.delta)/4, station = station[1], line_name=linename[1]) #, lat=lat[1], long=long[1] 

write.csv(entries_exits_rates, file = "subway_entries_exits.csv")

# average over all days, do not care about date
entries_exits_period <- group_by(entries_exits_rates, station_id, entry_exits_period) %>%
  summarise(hourly_entries = mean(hourly_entries),hourly_exits = mean(hourly_exits), station = station[1], line_name=line_name[1]) 

write.csv(entries_exits_period, file = "entries_exits_average.csv")
